import React, {useState} from "react";
import styled from "styled-components";

const Input = () => {
    const [state, setState] = useState({
        num1: Math.ceil(Math.random() * 10),
        num2: Math.ceil(Math.random() * 40),
        response: "",
        score: 0,
        message: ""
    });

    const inputKeyPress = (event) => {
        if (event.key === "Enter") {
            const answer = parseInt(state.response)
            console.log(answer);
            if (state.num1 + state.num2 === answer) {
                setState({
                    ...state,
                    response: "",
                    score: state.score + 1,
                    message: "That's correct!"
                })
            } else {

            }
        }
    }

    const updateResponse = (event) => {
        setState({
            ...state,
            response: event.target.value
        })
    }
    
    return (
        <Design>
            <Letter>{state.num1} + {state.num2}</Letter>
            <input onKeyPress={inputKeyPress} onChange={updateResponse} value={state.response} placeholder="Answer" />
            <p>{state.message}</p>
            <p>Score: {state.score}</p>
        </Design>
    );
}

const Design = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 20px;
    line-height: 30px;
`

const Letter = styled.label`
    font-size: 25px;
    margin-bottom: 20px;
`

export default Input;
