import React, {useState} from "react";
import styled from "styled-components";

const Input = () => {
    const [state, setState] = useState({
        num1: Math.ceil(Math.random() * 50),
        num2: Math.ceil(Math.random() * 150),
        response: "",
        score: 0
    });

    const inputKeyPress = (event) => {
        if (event === "Enter") {
            const answer = parseInt(state.response)
            if (state.num1 + state.num2 === answer) {
                setState({
                    ...state,
                    score: state.score + 1
                })
            } else {

            }
        }
    }

    const updateResponse = (event) => {
        setState({
            ...state,
            response: event.target.value
        })
    }
    
    return (
        <Design>
            <Letter>{state.num1} + {state.num2}</Letter>
            <input onKeyPress={inputKeyPress} onChange={updateResponse} placeholder="Answer" value={state.response} />
            <div>{state.score}</div>
        </Design>
    );
}

const Design = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 20px;
    line-height: 30px;
`

const Letter = styled.label`
    font-size: 25px;
    margin-bottom: 20px;
`

export default Input;
